import React from 'react'
import { api } from "../services/MockApiConfig"
import { Route, Redirect } from 'react-router-dom'
// import Overlay from './Overlay'

class Create extends React.Component {
    constructor() {
        super()
        this.state = {
            title: "Your Title",
            image: "",
            ingredients: "",
            directions: "",
            errorMsg: "",
            redirect: false
        }
    }

    handleTitleInput = event => {
        const title = event.target.value
        const image = event.target.value
        const ingredients = event.state.value
        const directions = event.state.value
        console.log(title)

        this.setState({
            title,
            image,
            ingredients,
            directions
        })
    }

    handleSubmit = event => {
        event.preventDefault()


        const { title, image, ingredients, directions } = this.state
        const data = {
            title,
            image,
            ingredients,
            directions
        }
        api.post("/cocktails", data)
            // .then((response) => response.status === 201 ? this.props.history.push("./cocktails") : null )
            .then(() => {
                this.setState({
                    redirect: true
                })
            })



            .catch(() => this.setState({ errorMsg: "An error occured, please refresh." }))



    }

    render() {

        const redirect = this.state.redirect && <Redirect to="/Overlay" />
        return (

            <div>
                {redirect}
                <div className="createHero">
                    <div className="createTitleBox">
                        <h1>Create Your Own Recipes</h1>
                    </div>
                </div>

                <div className="createDivider"></div>
                <div className="formBox">

                    <form onSubmit={this.handleSubmit}>
                        <h3>{this.state.title}</h3>

                        <h5>Image URL</h5>
                        <input type="text" name="image"></input>

                        <h5>Title</h5>
                        <input type="text" name="titleInput" onChange={this.handleTitleInput}></input>

                        <h5>Ingredients</h5>
                        <input type="text" name="ingredients"></input>

                        <h5>Directions</h5>
                        <input type="text" name="directions"></input><br></br>

                        <input type="submit" value="Save Cocktail" />

                    </form>
                </div>
            </div>
        )
    }
}

export default Create
