        super()
        this.state = {
            searchValue: "",
            results: [],
        }

        this.cancel = ""
    }

    componentDidMount() {
        this.fetchCocktailApi()
    }

    fetchCocktailApi = async () => {
        try {
            const apiCocktails = await api.get("search.php?s=cocktail")
            this.setState({apiCocktails: apiCocktails.data})
            console.log(apiCocktails)
        }
        catch(error) {
            console.error(error)
        }
    }

