        catch(error) {
            console.error(error)
        }
    }

    fetchSearchInput = () => {
        if (this.cancel) {
            this.cancel.cancel();
        }
        this.cancel = axios.CancelToken.source();
    }

    render() {
        // const { searchValue } = this.state;
        return (
            <div>
                <div className="searchDecoDivider"></div>
                <h2>Search Cocktails</h2>
