    componentDidMount() {
        this.fetchCocktailApi()
    }

    fetchCocktailApi = async () => {
        try {
            const cocktail = await api.get("search.php?s=cocktail")
            this.setState({cocktail: cocktail.data})
            console.log(cocktail)
        }
        catch(error) {
            console.error(error)
        }
    }

    render() {
        return (
            <div>
                <div className="searchDecoDivider"></div>
                <h2>Search Cocktails</h2>
                <span><input type="text" name="searchValue" onChange={this.handleInput} /><button type="submit" className="searchButton">Search Icon</button></span>
                <div className="searchCardBox"></div>
            </div>
        )
    }
}

export default Search
